let count = 0   

function switchCards() {
    let title = data["results"][count]["name"]["title"]
    let fname = data["results"][count]["name"]["first"]
    let lname = data["results"][count]["name"]["last"]
    let name = title + " " + fname + " " + lname
    let email = data["results"][count]["email"]
    let phone = data["results"][count]["phone"]
    let picture = data["results"][count]["picture"]["large"]

    //console.log(name)


    //INFO TO THE USER CARD
    //FIL WITH THE RIGHT TEXT 
    document.getElementsByClassName("name")[0].innerHTML = name

    document.getElementsByClassName("card-pic")[0].innerHTML = "<img src='" + picture + "'>"
    document.getElementsByTagName("img")[0].classList.add("class-img")

    //element.classList.add("mystyle");

    document.getElementsByClassName("email")[0].innerHTML = email
    document.getElementsByClassName("phone")[0].innerHTML = phone

    //console.log(data[1].name.title)
    //console.log(myResults[2])
}
switchCards()

// Button left
const left = document.getElementsByClassName("btn-left")[0]

left.addEventListener('click', function(){
    count--;
    if(count < 0) {
        count = data["results"].length - 1
    }
    switchCards()
})

// Button right
const right = document.getElementsByClassName("btn-right")[0]
right.addEventListener('click', function(){
    count++
    if(count == data["results"].length)
    count = 0
    switchCards()
})

